      })
      .then((res) => {
        console.log(res);
        setIsAuth(true);
        setLoading(false);
      })
      .catch((err) => {
        console.log(err);
        setIsAuth(false);
        setLoading(false);
      });
  }
  useEffect(() => {
    checktoken();
  }, []);
